// src/components/Dashboard.js
import React from 'react';
import { Container, Grid, Card, CardContent, Typography, Button } from '@mui/material';
import Sidebar from './SideBar';
import Navbar from './Navbar';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
    const navigate = useNavigate(); // For navigation
    const courses = [
        { name: "Math 135", description: "Find study groups for Math 135" },
        { name: "CS 135", description: "Find study groups for CS 135" },
    ];

    return (
        <div className="flex">
            <Sidebar />
            <div className="flex-1">
                <Navbar />
                <Container maxWidth="lg" className="mt-8">
                    <Grid container spacing={4} justifyContent="center">
                        {courses.map((course, index) => (
                            <Grid item xs={12} sm={6} md={4} key={index}>
                                <Card className="shadow-lg rounded-lg hover:shadow-xl transition-shadow duration-200 w-full h-72 bg-gradient-to-r from-green-400 to-blue-500">
                                    <CardContent className="flex flex-col justify-between h-full">
                                        <Typography variant="h5" className="font-semibold text-lg text-white text-center">
                                            {course.name}
                                        </Typography>
                                        <Typography variant="body2" className="text-gray-100 text-center">
                                            {course.description}
                                        </Typography>
                                        <Button
                                            variant="contained"
                                            className="mt-4 bg-white text-blue-500 hover:bg-gray-100 transition-colors duration-200 w-full"
                                            onClick={() => navigate(`/course/${course.name}`)}
                                        >
                                            Find Study Groups
                                        </Button>
                                    </CardContent>
                                </Card>
                            </Grid>
                        ))}
                    </Grid>
                </Container>
            </div>
        </div>
    );
};

export default Dashboard;
