import React from 'react';
import { Container, Grid, Card, CardContent, Typography, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const courses = [
    { name: 'Math 135', description: 'Algebra for Honours Mathematics' },
    { name: 'CS 135', description: 'Designing Functional Programs' }
];

const Dashboard = () => {
    const navigate = useNavigate();


    return (
        <Container maxWidth="lg" className="dashboard-container mt-10 flex flex-col items-center justify-center h-full">
            <Card className="shadow-lg rounded-lg hover:shadow-xl transition-shadow duration-200 w-full h-[75vh] flex items-center justify-center">
                <Typography variant="h4" className="text-center font-bold text-white text-5xl">
                    Welcome to Study Buddy
                </Typography>
            </Card>
            <Grid container spacing={4} justifyContent="center" className="mt-8">
                {courses.map((course, index) => (
                    <Grid item xs={12} sm={6} md={4} key={index} className="flex justify-center">
                        <Card className="shadow-lg rounded-lg hover:shadow-xl transition-shadow duration-200 w-full h-72"> {/* Individual cards */}
                            <CardContent className="flex flex-col justify-between h-full">
                                <Typography variant="h5" className="font-semibold text-lg text-white text-center">
                                    {course.name}
                                </Typography>
                                <Typography variant="body2" className="text-gray-200 text-center">
                                    {course.description}
                                </Typography>
                                <Button
                                    variant="contained"
                                    className="mt-4 bg-blue-500 text-white hover:bg-blue-600 transition-colors duration-200 w-full"
                                    onClick={() => navigate(`/course/${course.name}`)}
                                >
                                    Find Study Groups
                                </Button>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );



};

export default Dashboard;
