// src/components/Dashboard.js
import React from 'react';
import { Container, Card, Typography, Grid, Button } from '@mui/material';

const courses = [
    { name: 'Math 135', description: 'Find study groups for Math 135' },
    { name: 'CS 135', description: 'Join study groups for CS 135' },
];

const Dashboard = () => {
    return (
        <Container maxWidth="lg" className="mt-10">
            <Card className="shadow-lg rounded-lg h-[75vh] flex items-center justify-center">
                <Typography variant="h4" className="text-center font-bold text-white text-5xl">
                    We help you find study buddies
                </Typography>
            </Card>
            <Grid container spacing={4} justifyContent="center" className="mt-8">
                {courses.map((course, index) => (
                    <Grid item xs={12} sm={6} md={4} key={index}>
                        <Card className="shadow-lg rounded-lg h-72 flex flex-col justify-between p-4">
                            <Typography variant="h5" className="font-semibold text-lg text-center">
                                {course.name}
                            </Typography>
                            <Typography variant="body2" className="text-center">
                                {course.description}
                            </Typography>
                            <Button variant="contained" color="primary" className="mt-4">
                                Find Study Groups
                            </Button>
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default Dashboard;
