// src/components/Dashboard.js
import React from 'react';
import { Link } from 'react-router-dom';

const Dashboard = () => {
    const courses = [
        { name: 'Math 135', description: 'Find study groups for Math 135' },
        { name: 'CS 135', description: 'Find study groups for CS 135' },
    ];

    return (
        <div className="flex flex-col items-center mt-10">
            <div className="bg-gray-800 text-white py-6 px-8 rounded-lg w-3/4 text-center shadow-lg">
                <h2 className="text-2xl font-bold">Courses</h2>
            </div>
            <div className="flex flex-wrap justify-center mt-6">
                {courses.map((course, index) => (
                    <Link to={`/course/${course.name}`} key={index} className="m-4">
                        <div className="bg-gray-700 text-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200 w-64 text-center">
                            <h3 className="text-xl font-semibold">{course.name}</h3>
                            <p className="mt-2">{course.description}</p>
                        </div>
                    </Link>
                ))}
            </div>
        </div>
    );
};

export default Dashboard;
