import React from 'react';
import './Dashboard.css'; // Reuse the styles from Dashboard if you want

const studyGroups = [
    { groupName: 'Math 135 Study Group 1', members: 5, meetingTime: 'Fridays at 4 PM' },
    { groupName: 'CS 135 Study Group 1', members: 7, meetingTime: 'Sundays at 3 PM' }
];

const CoursePage = () => {
    return (
        <div className="dashboard-container">
            <h1 className="dashboard-title">Study Groups for {courseName}</h1>
            {studyGroups.map((group, index) => (
                <div key={index} className="course-card">
                    <h2 className="course-title">{group.groupName}</h2>
                    <p className="course-description">Members: {group.members}</p>
                    <p className="course-description">Meeting Time: {group.meetingTime}</p>
                    <button className="find-study-group-btn">Join Group</button>
                </div>
            ))}
        </div>
    );
};

export default CoursePage;
